import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import walletService from '../services/walletService';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  
  const [wallet, setWallet] = useState(null);
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showCreateWallet, setShowCreateWallet] = useState(false);
  const [showTopUp, setShowTopUp] = useState(false);
  const [showTransfer, setShowTransfer] = useState(false);
  
  // Form states
  const [topUpAmount, setTopUpAmount] = useState('');
  const [transferAmount, setTransferAmount] = useState('');
  const [receiverWallet, setReceiverWallet] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadWalletData();
  }, []);

  const loadWalletData = async () => {
    try {
      setLoading(true);
      const walletInfo = await walletService.getWalletInfo();
      setWallet(walletInfo);
      
      const balanceData = await walletService.getBalance();
      setBalance(balanceData.balance);
      
      const transactionData = await walletService.getTransactions();
      setTransactions(transactionData);
      
      // Show create wallet button only if wallet number is missing or empty
      if (!walletInfo || !walletInfo.walletNumber) {
        setShowCreateWallet(true);
      } else {
        setShowCreateWallet(false);
      }
    } catch (error) {
      // Convert error to string for includes check
      const errorMessage = typeof error === 'string' ? error : JSON.stringify(error);
      if (errorMessage.includes('Wallet not found')) {
        setShowCreateWallet(true);
      } else {
        console.error('Error loading wallet data:', error);
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCreateWallet = async () => {
    try {
      setError('');
      setSuccess('');
      const newWallet = await walletService.createWallet();
      setWallet(newWallet);
      setBalance(0);
      setShowCreateWallet(false);
      setSuccess('Wallet created successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleTopUp = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      await walletService.topUpWallet(parseFloat(topUpAmount));
      await loadWalletData();
      setTopUpAmount('');
      setShowTopUp(false);
      setSuccess('Wallet topped up successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleTransfer = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      await walletService.transferMoney(receiverWallet, parseFloat(transferAmount));
      await loadWalletData();
      setTransferAmount('');
      setReceiverWallet('');
      setShowTransfer(false);
      setSuccess('Money transferred successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return (
      <div className="dashboard">
        <div className="loading">Loading wallet data...</div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>E-Wallet Dashboard</h1>
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          <span>{error}</span>
          <button onClick={() => setError('')}>×</button>
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <span>{success}</span>
          <button onClick={() => setSuccess('')}>×</button>
        </div>
      )}

      <div className="dashboard-content">
        <div className="user-info">
          <h2>User Information</h2>
          <p><strong>Name:</strong> {user.fullName || 'N/A'}</p>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
        </div>

        {showCreateWallet ? (
          <div className="create-wallet-section">
            <h2>Create Your Wallet</h2>
            <p>You don't have a wallet yet. Create one to start using our services.</p>
            <button onClick={handleCreateWallet} className="btn btn-primary">
              Create Wallet
            </button>
          </div>
        ) : (
          <>
            <div className="wallet-info">
              <h2>Wallet Information</h2>
              <div className="wallet-card">
                <h3>Wallet Number</h3>
                <p className="wallet-number">{wallet?.walletNumber}</p>
              </div>
              <div className="wallet-card">
                <h3>Current Balance</h3>
                <p className="balance">{formatCurrency(balance)}</p>
              </div>
            </div>

            <div className="action-buttons">
              <button 
                onClick={() => setShowTopUp(true)} 
                className="btn btn-success"
              >
                Add Money
              </button>
              <button 
                onClick={() => setShowTransfer(true)} 
                className="btn btn-primary"
              >
                Send Money
              </button>
              <button 
                onClick={() => loadWalletData()} 
                className="btn btn-secondary"
              >
                Refresh
              </button>
            </div>

            {showTopUp && (
              <div className="modal">
                <div className="modal-content">
                  <h3>Top Up Wallet</h3>
                  <form onSubmit={handleTopUp}>
                    <div className="form-group">
                      <label>Amount</label>
                      <input
                        type="number"
                        step="0.01"
                        min="0.01"
                        value={topUpAmount}
                        onChange={(e) => setTopUpAmount(e.target.value)}
                        required
                      />
                    </div>
                    <div className="form-actions">
                      <button type="submit" className="btn btn-success">Add Money</button>
                      <button type="button" onClick={() => setShowTopUp(false)} className="btn btn-secondary">Cancel</button>
                    </div>
                  </form>
                </div>
              </div>
            )}

            {showTransfer && (
              <div className="modal">
                <div className="modal-content">
                  <h3>Send Money</h3>
                  <form onSubmit={handleTransfer}>
                    <div className="form-group">
                      <label>Receiver Wallet Number</label>
                      <input
                        type="text"
                        value={receiverWallet}
                        onChange={(e) => setReceiverWallet(e.target.value)}
                        required
                      />
                    </div>
                    <div className="form-group">
                      <label>Amount</label>
                      <input
                        type="number"
                        step="0.01"
                        min="0.01"
                        value={transferAmount}
                        onChange={(e) => setTransferAmount(e.target.value)}
                        required
                      />
                    </div>
                    <div className="form-actions">
                      <button type="submit" className="btn btn-primary">Send Money</button>
                      <button type="button" onClick={() => setShowTransfer(false)} className="btn btn-secondary">Cancel</button>
                    </div>
                  </form>
                </div>
              </div>
            )}

            <div className="transaction-history">
              <h2>Recent Transactions</h2>
              {transactions.length === 0 ? (
                <p>No transactions yet</p>
              ) : (
                <table>
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Type</th>
                      <th>Amount</th>
                      <th>From</th>
                      <th>To</th>
                    </tr>
                  </thead>
                  <tbody>
                    {transactions.map((txn) => (
                      <tr key={txn.id}>
                        <td>{formatDate(txn.transactionDate)}</td>
                        <td>{txn.transactionType}</td>
                        <td>{formatCurrency(txn.amount)}</td>
                        <td>{txn.senderWallet.walletNumber}</td>
                        <td>{txn.receiverWallet.walletNumber}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
