import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { 
  Wallet, 
  Plus, 
  Send, 
  RefreshCw, 
  LogOut, 
  User, 
  Mail, 
  CreditCard, 
  DollarSign,
  ArrowUpRight,
  ArrowDownLeft,
  Clock,
  X,
  TrendingUp,
  Eye,
  EyeOff,
  ChevronRight,
  Activity
} from 'lucide-react';
import walletService from '../services/walletService';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  
  const [wallet, setWallet] = useState(null);
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showTopUp, setShowTopUp] = useState(false);
  const [showTransfer, setShowTransfer] = useState(false);
  const [showBalance, setShowBalance] = useState(true);
  
  // Form states
  const [topUpAmount, setTopUpAmount] = useState('');
  const [transferAmount, setTransferAmount] = useState('');
  const [receiverWallet, setReceiverWallet] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadWalletData();
  }, []);

  const loadWalletData = async () => {
    try {
      setLoading(true);
      let walletInfo = await walletService.getWalletInfo(user.id);
      if (!walletInfo) {
        walletInfo = await walletService.createWallet(user.id);
      }
      setWallet(walletInfo);
      
      const balanceData = await walletService.getBalance(user.id);
      setBalance(balanceData.balance);
      
      const transactionData = await walletService.getTransactions(user.id);
      setTransactions(transactionData);
      
    } catch (error) {
      console.error('Error loading wallet data:', error);
      if (error && error.message) {
        setError(`Failed to load wallet data: ${error.message}`);
      } else if (typeof error === 'string') {
        setError(error);
      } else {
        setError('Failed to load wallet data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleTopUp = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      await walletService.topUpWallet(user.id, parseFloat(topUpAmount));
      await loadWalletData();
      setTopUpAmount('');
      setShowTopUp(false);
      setSuccess('Wallet topped up successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleTransfer = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      await walletService.transferMoney(user.id, receiverWallet, parseFloat(transferAmount));
      await loadWalletData();
      setTransferAmount('');
      setReceiverWallet('');
      setShowTransfer(false);
      setSuccess('Money transferred successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="dashboard-loading">
        <div className="loading-content">
          <div className="loading-spinner"></div>
          <p className="loading-text">Loading your wallet...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      {/* Floating background shapes */}
      <div className="floating-background">
        <div className="floating-shape shape-1"></div>
        <div className="floating-shape shape-2"></div>
        <div className="floating-shape shape-3"></div>
      </div>

      <div className="dashboard-content">
        {/* Header */}
        <header className="dashboard-header">
          <div className="header-content">
            <div className="header-left">
              <div className="logo-section">
                <div className="logo-icon">
                  <Wallet size={24} />
                </div>
                <div className="logo-text">
                  <h1 className="app-title">WalletPro</h1>
                  <p className="app-subtitle">Digital Wallet</p>
                </div>
              </div>
            </div>
            <button onClick={handleLogout} className="logout-button">
              <LogOut size={16} />
              <span>Logout</span>
            </button>
          </div>
        </header>

        {/* Alerts */}
        {error && (
          <div className="alert alert-error">
            <div className="alert-content">
              <div className="alert-icon">
                <X size={16} />
              </div>
              <p>{error}</p>
            </div>
            <button onClick={() => setError('')} className="alert-close">
              <X size={16} />
            </button>
          </div>
        )}

        {success && (
          <div className="alert alert-success">
            <div className="alert-content">
              <div className="alert-icon">
                <TrendingUp size={16} />
              </div>
              <p>{success}</p>
            </div>
            <button onClick={() => setSuccess('')} className="alert-close">
              <X size={16} />
            </button>
          </div>
        )}

        <main className="main-content">
          {/* Welcome Section */}
          <div className="welcome-section">
            <h2 className="welcome-title">
              Welcome back, {user.fullName || user.username}!
            </h2>
            <p className="welcome-subtitle">Here's what's happening with your wallet today.</p>
          </div>

          {/* Main Grid */}
          <div className="dashboard-grid">
            {/* Balance Card */}
            <div className="balance-section">
              <div className="balance-card">
                <div className="balance-header">
                  <div className="balance-info">
                    <div className="balance-icon">
                      <CreditCard size={24} />
                    </div>
                    <div className="balance-details">
                      <p className="balance-label">Total Balance</p>
                      <p className="wallet-number">Wallet #{wallet?.walletNumber}</p>
                    </div>
                  </div>
                  <button 
                    onClick={() => setShowBalance(!showBalance)}
                    className="balance-toggle"
                  >
                    {showBalance ? <Eye size={16} /> : <EyeOff size={16} />}
                  </button>
                </div>
                <div className="balance-amount">
                  <p className="amount">
                    {showBalance ? formatCurrency(balance) : '••••••'}
                  </p>
                  <p className="amount-subtitle">
                    <TrendingUp size={16} />
                    Available for spending
                  </p>
                </div>
                <div className="balance-actions">
                  <button
                    onClick={() => setShowTopUp(true)}
                    className="action-button add-money"
                  >
                    <Plus size={16} />
                    <span>Add Money</span>
                  </button>
                  <button
                    onClick={() => setShowTransfer(true)}
                    className="action-button send-money"
                  >
                    <Send size={16} />
                    <span>Send</span>
                  </button>
                  <button
                    onClick={loadWalletData}
                    className="action-button refresh"
                  >
                    <RefreshCw size={16} />
                  </button>
                </div>
              </div>
            </div>

            {/* Profile Card */}
            <div className="profile-section">
              <div className="profile-card">
                <div className="profile-header">
                  <div className="profile-icon">
                    <User size={24} />
                  </div>
                  <div className="profile-title">
                    <h3>Profile</h3>
                    <p>Account details</p>
                  </div>
                </div>
                <div className="profile-details">
                  <div className="profile-item">
                    <User size={16} />
                    <div className="profile-item-content">
                      <p className="profile-value">{user.fullName || 'Not provided'}</p>
                      <p className="profile-label">Full Name</p>
                    </div>
                  </div>
                  <div className="profile-item">
                    <User size={16} />
                    <div className="profile-item-content">
                      <p className="profile-value">{user.username}</p>
                      <p className="profile-label">Username</p>
                    </div>
                  </div>
                  <div className="profile-item">
                    <Mail size={16} />
                    <div className="profile-item-content">
                      <p className="profile-value">{user.email}</p>
                      <p className="profile-label">Email Address</p>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* Transactions */}
          <div className="transactions-section">
            <div className="transactions-card">
              <div className="transactions-header">
                <div className="transactions-title">
                  <div className="transactions-icon">
                    <Activity size={24} />
                  </div>
                  <div className="transactions-text">
                    <h3>Recent Transactions</h3>
                    <p>Your latest wallet activity</p>
                  </div>
                </div>
                <button
                  onClick={loadWalletData}
                  className="refresh-button"
                >
                  Refresh
                </button>
              </div>
              <div className="transactions-content">
                {transactions.length === 0 ? (
                  <div className="no-transactions">
                    <div className="no-transactions-icon">
                      <Activity size={32} />
                    </div>
                    <p className="no-transactions-title">No transactions yet</p>
                    <p className="no-transactions-subtitle">Your transaction history will appear here</p>
                  </div>
                ) : (
                  <div className="transactions-list">
                    {transactions.slice(0, 5).map((txn) => (
                      <div key={txn.id} className="transaction-item">
                        <div className="transaction-left">
                          <div className={`transaction-icon ${
                            txn.transactionType === 'TOPUP' ? 'topup' : 'transfer'
                          }`}>
                            {txn.transactionType === 'TOPUP' ? (
                              <ArrowDownLeft size={16} />
                            ) : (
                              <ArrowUpRight size={16} />
                            )}
                          </div>
                          <div className="transaction-details">
                            <p className="transaction-type">
                              {txn.transactionType === 'TOPUP' ? 'Money Added' : 'Money Sent'}
                            </p>
                            <div className="transaction-date">
                              <Clock size={12} />
                              <span>{formatDate(txn.transactionDate)}</span>
                            </div>
                          </div>
                        </div>
                        <div className="transaction-right">
                          <p className={`transaction-amount ${
                            txn.transactionType === 'TOPUP' ? 'positive' : 'negative'
                          }`}>
                            {txn.transactionType === 'TOPUP' ? '+' : '-'}{formatCurrency(txn.amount)}
                          </p>
                          <p className="transaction-target">
                            {txn.transactionType === 'TRANSFER' ? 
                              `To: ${txn.receiverWallet?.walletNumber}` : 
                              'Top-up'
                            }
                          </p>
                        </div>
                      </div>
                    ))}
                    {transactions.length > 5 && (
                      <button className="view-all-button">
                        <span>View all transactions</span>
                        <ChevronRight size={16} />
                      </button>
                    )}
                  </div>
                )}
              </div>
            </div>
          </div>
        </main>
      </div>

      {/* Top Up Modal */}
      {showTopUp && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <div className="modal-title">
                <div className="modal-icon topup">
                  <Plus size={20} />
                </div>
                <h3>Add Money</h3>
              </div>
              <button
                onClick={() => setShowTopUp(false)}
                className="modal-close"
              >
                <X size={20} />
              </button>
            </div>
            <form onSubmit={handleTopUp}>
              <div className="modal-content">
                <div className="form-group">
                  <label>Amount to Add</label>
                  <div className="input-wrapper">
                    <DollarSign size={20} className="input-icon" />
                    <input
                      type="number"
                      step="0.01"
                      min="0"
                      value={topUpAmount}
                      onChange={(e) => setTopUpAmount(e.target.value)}
                      required
                      placeholder="0.00"
                      className="modal-input"
                    />
                  </div>
                </div>
              </div>
              <div className="modal-actions">
                <button
                  type="button"
                  onClick={() => setShowTopUp(false)}
                  className="modal-button cancel"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="modal-button submit topup"
                >
                  Add Money
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* Transfer Modal */}
      {showTransfer && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <div className="modal-title">
                <div className="modal-icon transfer">
                  <Send size={20} />
                </div>
                <h3>Send Money</h3>
              </div>
              <button
                onClick={() => setShowTransfer(false)}
                className="modal-close"
              >
                <X size={20} />
              </button>
            </div>
            <form onSubmit={handleTransfer}>
              <div className="modal-content">
                <div className="form-group">
                  <label>Receiver Email</label>
                  <div className="input-wrapper">
                    <Mail size={20} className="input-icon" />
                    <input
                      type="email"
                      value={receiverWallet}
                      onChange={(e) => setReceiverWallet(e.target.value)}
                      required
                      placeholder="recipient@email.com"
                      className="modal-input"
                    />
                  </div>
                </div>
                <div className="form-group">
                  <label>Amount to Send</label>
                  <div className="input-wrapper">
                    <DollarSign size={20} className="input-icon" />
                    <input
                      type="number"
                      step="0.01"
                      min="0"
                      value={transferAmount}
                      onChange={(e) => setTransferAmount(e.target.value)}
                      required
                      placeholder="0.00"
                      className="modal-input"
                    />
                  </div>
                </div>
              </div>
              <div className="modal-actions">
                <button
                  type="button"
                  onClick={() => setShowTransfer(false)}
                  className="modal-button cancel"
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="modal-button submit transfer"
                >
                  Send Money
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
