import axios from 'axios';

const API_URL = 'http://localhost:8080/api/wallet';

const getAuthHeader = () => {
  const token = localStorage.getItem('token');
  return { Authorization: `Bearer ${token}` };
};

const walletService = {
  createWallet: async () => {
    try {
      const response = await axios.post(`${API_URL}/create`, {}, {
        headers: getAuthHeader()
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || 'Failed to create wallet';
    }
  },

  getBalance: async () => {
    try {
      const response = await axios.get(`${API_URL}/balance`, {
        headers: getAuthHeader()
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || 'Failed to get balance';
    }
  },

  topUpWallet: async (amount) => {
    try {
      const response = await axios.post(`${API_URL}/topup`, { amount }, {
        headers: getAuthHeader()
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || 'Failed to top up wallet';
    }
  },

  transferMoney: async (receiverWalletNumber, amount) => {
    try {
      const response = await axios.post(`${API_URL}/transfer`, {
        receiverWalletNumber,
        amount
      }, {
        headers: getAuthHeader()
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || 'Failed to transfer money';
    }
  },

  getTransactions: async () => {
    try {
      const response = await axios.get(`${API_URL}/transactions`, {
        headers: getAuthHeader()
      });
      return response.data;
    } catch (error) {
      throw error.response?.data || 'Failed to get transactions';
    }
  },

  // getWalletInfo: async () => {
  //   try {
  //     const response = await axios.get(`${API_URL}/wallet-info`, {
  //       headers: getAuthHeader()
  //     });
  //     return response.data;
  //   } catch (error) {
  //     throw error.response?.data || 'Failed to get wallet info';
  //   }
  // }
  // reactapp/src/services/walletService.js
  getWalletInfo: async () => {
  try {
    // read user from localStorage (your Dashboard already stores it)
    const userStr = localStorage.getItem('user');
    const user = userStr ? JSON.parse(userStr) : null;
    const headers = {
      ...getAuthHeader(),
      // also send username as a fallback for the server-to-identify-user
      'X-Username': user?.username || ''
    };

    const response = await axios.get(`${API_URL}/wallet-info`, {
      headers
    });
    return response.data;
  } catch (error) {
    // keep existing error behavior
    throw error.response?.data || 'Failed to get wallet info';
  }
}

};

export default walletService;
