import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import walletService from '../services/walletService';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  
  const [wallet, setWallet] = useState(null);
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showTopUp, setShowTopUp] = useState(false);
  const [showTransfer, setShowTransfer] = useState(false);
  
  // Form states
  const [topUpAmount, setTopUpAmount] = useState('');
  const [transferAmount, setTransferAmount] = useState('');
  const [receiverWallet, setReceiverWallet] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  useEffect(() => {
    loadWalletData();
  }, [loadWalletData]);

  const loadWalletData = async () => {
    try {
      setLoading(true);
      // Ensure wallet exists or create it
      await walletService.createWallet(user.id);

      const walletInfo = await walletService.getWalletInfo(user.id);
      setWallet(walletInfo);
      
      const balanceData = await walletService.getBalance(user.id);
      setBalance(balanceData.balance);
      
      const transactionData = await walletService.getTransactions(user.id);
      setTransactions(transactionData);
      
    } catch (error) {
      console.error('Error loading wallet data:', error);
      if (error && error.message) {
        setError(`Failed to load wallet data: ${error.message}`);
      } else if (typeof error === 'string') {
        setError(error);
      } else {
        setError('Failed to load wallet data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleTopUp = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      await walletService.topUpWallet(user.id, parseFloat(topUpAmount));
      await loadWalletData();
      setTopUpAmount('');
      setShowTopUp(false);
      setSuccess('Wallet topped up successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleTransfer = async (e) => {
    e.preventDefault();
    try {
      setError('');
      setSuccess('');
      await walletService.transferMoney(user.id, receiverWallet, parseFloat(transferAmount));
      await loadWalletData();
      setTransferAmount('');
      setReceiverWallet('');
      setShowTransfer(false);
      setSuccess('Money transferred successfully!');
    } catch (error) {
      setError(error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/login');
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD'
    }).format(amount);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString();
  };

  if (loading) {
    return (
      <div className="dashboard">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <p>Loading wallet data...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>E-Wallet Dashboard</h1>
        <button onClick={handleLogout} className="logout-btn">
          Logout
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          <span>{error}</span>
          <button onClick={() => setError('')}>×</button>
        </div>
      )}

      {success && (
        <div className="alert alert-success">
          <span>{success}</span>
          <button onClick={() => setSuccess('')}>×</button>
        </div>
      )}

      <div className="dashboard-content">
        <div className="user-info">
          <h2>User Information</h2>
          <p><strong>Name:</strong> {user.fullName || 'N/A'}</p>
          <p><strong>Username:</strong> {user.username}</p>
          <p><strong>Email:</strong> {user.email}</p>
        </div>

        <div className="wallet-info">
          <h2>Wallet Information</h2>
          <div className="wallet-card">
            <h3>Wallet Number</h3>
            <p className="wallet-number">{wallet?.walletNumber}</p>
          </div>
          <div className="wallet-card">
            <h3>Current Balance</h3>
            <p className="balance">{formatCurrency(balance)}</p>
          </div>
        </div>

        <div className="action-buttons">
          <button 
            onClick={() => setShowTopUp(true)} 
            className="btn btn-success"
          >
            Add Money
          </button>
          <button 
            onClick={() => setShowTransfer(true)} 
            className="btn btn-primary"
          >
            Send Money
          </button>
          <button 
            onClick={() => loadWalletData()} 
            className="btn btn-secondary"
          >
            Refresh
          </button>
        </div>

        <div className="transaction-history">
          <h2>Recent Transactions</h2>
          {transactions.length === 0 ? (
            <p>No transactions yet</p>
          ) : (
            <table>
              <thead>
                <tr>
                  <th>Date</th>
                  <th>Type</th>
                  <th>Amount</th>
                  <th>From</th>
                  <th>To</th>
                </tr>
              </thead>
              <tbody>
                {transactions.map((txn) => (
                  <tr key={txn.id}>
                    <td>{formatDate(txn.transactionDate)}</td>
                    <td>{txn.transactionType}</td>
                    <td>{formatCurrency(txn.amount)}</td>
                    <td>{txn.senderWallet?.walletNumber || 'N/A'}</td>
                    <td>{txn.receiverWallet?.walletNumber || 'N/A'}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        {/* Top Up Modal */}
        {showTopUp && (
          <div className="modal-overlay">
            <div className="modal">
              <div className="modal-header">
                <h3>Add Money to Wallet</h3>
                <button onClick={() => setShowTopUp(false)} className="close-btn">
                  ×
                </button>
              </div>
              <form onSubmit={handleTopUp}>
                <div className="form-group">
                  <label>Amount</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    value={topUpAmount}
                    onChange={(e) => setTopUpAmount(e.target.value)}
                    required
                    placeholder="Enter amount"
                  />
                </div>
                <div className="modal-actions">
                  <button type="button" onClick={() => setShowTopUp(false)}>
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-success">
                    Add Money
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Transfer Modal */}
        {showTransfer && (
          <div className="modal-overlay">
            <div className="modal">
              <div className="modal-header">
                <h3>Send Money</h3>
                <button onClick={() => setShowTransfer(false)} className="close-btn">
                  ×
                </button>
              </div>
              <form onSubmit={handleTransfer}>
                <div className="form-group">
                  <label>Receiver Wallet Number</label>
                  <input
                    type="text"
                    value={receiverWallet}
                    onChange={(e) => setReceiverWallet(e.target.value)}
                    required
                    placeholder="Enter wallet number"
                  />
                </div>
                <div className="form-group">
                  <label>Amount</label>
                  <input
                    type="number"
                    step="0.01"
                    min="0"
                    value={transferAmount}
                    onChange={(e) => setTransferAmount(e.target.value)}
                    required
                    placeholder="Enter amount"
                  />
                </div>
                <div className="modal-actions">
                  <button type="button" onClick={() => setShowTransfer(false)}>
                    Cancel
                  </button>
                  <button type="submit" className="btn btn-primary">
                    Send Money
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
